import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000" });

export const register = (userData) => API.post("/register", userData);
export const login = (userData) => API.post("/login", userData);
export const fetchBooks = () => API.get("/books");
export const addBook = (bookData, token) =>
  API.post("/books", bookData, { headers: { Authorization: token } });
export const editBook = (id, bookData, token) =>
  API.put(`/books/${id}`, bookData, { headers: { Authorization: token } });
export const deleteBook = (id, token) =>
  API.delete(`/books/${id}`, { headers: { Authorization: token } });
export const addReview = (id, reviewData) => API.post(`/books/${id}/reviews`, reviewData); // Updated endpoint for adding reviews
export const updateReview = (bookId, reviewId, reviewData, token) => 
  API.put(`/books/${bookId}/reviews/${reviewId}`, reviewData, { headers: { Authorization: token } });
export const deleteReview = (bookId, reviewId, token) => 
  API.delete(`/books/${bookId}/reviews/${reviewId}`, { headers: { Authorization: token } });
