import React, { useEffect, useState } from "react";
import { fetchBooks, deleteReview } from "../api";
import { useNavigate } from "react-router";

function Main() {
  const [books, setBooks] = useState([]);
  const [review, setReview] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  const handleDeleteReview = async (bookId, reviewId) => {
    const token = localStorage.getItem("token");

    const config = {
      headers: { Authorization: token },
    };

    try {
      await deleteReview(bookId, reviewId, config);
      alert("Review deleted!");
      window.location.reload();
    } catch (err) {
      alert("Error deleting review");
    }
  };

  return (
    <div>
      <button onClick={handleLogout}>Logout</button>
      <h2>Book List</h2>
      {books.map((book) => (
        <div key={book._id}>
          <h3>{book.title}</h3>
          <p>Author: {book.author}</p>
          <h4>Reviews:</h4>
          {book.reviews.map((rev, index) => (
            <div key={index}>
              <p>
                {rev.reviewer}: {rev.comment}
              </p>
              {rev.reviewer === localStorage.getItem("username") && (
                <button
                  onClick={() => handleDeleteReview(book._id, index)}
                >
                  Delete Review
                </button>
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default Main;
