// Login.js

import React, { useState } from 'react';
import { loginUser } from '../api/api'; // Correct import
import { useHistory } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { setUser } = useAuth(); // Set user in context on login
  const history = useHistory();
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(username, password); // Call loginUser instead of login
      setUser(response.data); // Set the user data in context
      history.push('/'); // Redirect to home page after login
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Username</label>
        <input 
          type="text" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
        />
        <label>Password</label>
        <input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
