import React, { useState, useEffect } from "react";
import { fetchBookDetails, updateReview, deleteReview } from "../api";

function BookDetails({ bookId }) {
  const [book, setBook] = useState({});
  const [reviews, setReviews] = useState([]);
  const [editingReview, setEditingReview] = useState(null); // State to track review being edited
  const [newReview, setNewReview] = useState("");
  const token = localStorage.getItem("token");
  const username = localStorage.getItem("username");

  useEffect(() => {
    const getBookDetails = async () => {
      const { data } = await fetchBookDetails(bookId);
      setBook(data);
      setReviews(data.reviews);
    };
    getBookDetails();
  }, [bookId]);

  const handleEditReview = (review) => {
    setEditingReview(review);
    setNewReview(review.text);
  };

  const handleUpdateReview = async () => {
    try {
      await updateReview(editingReview._id, { text: newReview }, token);
      setReviews((prevReviews) =>
        prevReviews.map((review) =>
          review._id === editingReview._id ? { ...review, text: newReview } : review
        )
      );
      setEditingReview(null);
      setNewReview("");
    } catch (err) {
      alert("Error updating review");
    }
  };

  const handleDeleteReview = async (reviewId) => {
    try {
      await deleteReview(reviewId, token);
      setReviews(reviews.filter((review) => review._id !== reviewId));
    } catch (err) {
      alert("Error deleting review");
    }
  };

  const handleAddReview = async () => {
    try {
      const review = { text: newReview, username };
      // Call the API to add the review
      await addReview(bookId, review, token);
      setReviews([...reviews, review]);
      setNewReview(""); // Clear review input
    } catch (err) {
      alert("Error adding review");
    }
  };

  return (
    <div>
      <h2>{book.title} - Details</h2>
      <p>{book.author}</p>

      <h3>Reviews</h3>
      {reviews.map((review) => (
        <div key={review._id}>
          <p>{review.text}</p>
          <p>By: {review.username}</p>
          {review.username === username && ( // Show edit/delete buttons only for user's own reviews
            <>
              <button onClick={() => handleEditReview(review)}>Edit</button>
              <button onClick={() => handleDeleteReview(review._id)}>Delete</button>
            </>
          )}
        </div>
      ))}

      {editingReview ? (
        <div>
          <h3>Edit Review</h3>
          <textarea
            value={newReview}
            onChange={(e) => setNewReview(e.target.value)}
          />
          <button onClick={handleUpdateReview}>Update Review</button>
          <button onClick={() => setEditingReview(null)}>Cancel</button>
        </div>
      ) : (
        <div>
          <textarea
            value={newReview}
            onChange={(e) => setNewReview(e.target.value)}
            placeholder="Write your review..."
          />
          <button onClick={handleAddReview}>Add Review</button>
        </div>
      )}
    </div>
  );
}

export default BookDetails;
