import React, { useState, useEffect } from "react";
import { fetchBooks, addBook, deleteBook, editBook, searchBooks } from "../api";

function BookCRUD() {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [bookDetails, setBookDetails] = useState({
    title: "",
    author: "",
    isbn: "",
    genre: "",
    coverImage: "",
  });
  const [editingBook, setEditingBook] = useState(null);
  const token = localStorage.getItem("token");

  useEffect(() => {
    loadBooks();
  }, []);

  const loadBooks = async () => {
    const { data } = await fetchBooks();
    setBooks(data);
  };

  const handleAddBook = async () => {
    try {
      await addBook(bookDetails, token);
      alert("Book added successfully!");
      loadBooks();
    } catch (err) {
      alert("Error adding book");
    }
  };

  const handleEditBook = async () => {
    try {
      await editBook(editingBook._id, bookDetails, token);
      alert("Book updated successfully!");
      setEditingBook(null);
      loadBooks();
    } catch (err) {
      alert("Error editing book");
    }
  };

  const handleDeleteBook = async (id) => {
    try {
      await deleteBook(id, token);
      alert("Book deleted successfully!");
      loadBooks();
    } catch (err) {
      alert("Error deleting book");
    }
  };

  const handleSearch = async () => {
    const { data } = await searchBooks(searchQuery);
    setBooks(data);
  };

  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append("image", e.target.files[0]);
    const response = await fetch("/api/upload", {
      method: "POST",
      body: formData,
    });
    const result = await response.json();
    setBookDetails({ ...bookDetails, coverImage: result.url });
  };

  return (
    <div>
      <h2>Admin: Book CRUD</h2>
      <input
        type="text"
        placeholder="Search by title, author, or ISBN"
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      <h3>{editingBook ? "Edit Book" : "Add Book"}</h3>
      <input
        type="text"
        placeholder="Title"
        value={bookDetails.title}
        onChange={(e) => setBookDetails({ ...bookDetails, title: e.target.value })}
      />
      <input
        type="text"
        placeholder="Author"
        value={bookDetails.author}
        onChange={(e) => setBookDetails({ ...bookDetails, author: e.target.value })}
      />
      <input
        type="text"
        placeholder="ISBN"
        value={bookDetails.isbn}
        onChange={(e) => setBookDetails({ ...bookDetails, isbn: e.target.value })}
      />
      <input
        type="text"
        placeholder="Genre"
        value={bookDetails.genre}
        onChange={(e) => setBookDetails({ ...bookDetails, genre: e.target.value })}
      />
      <input type="file" onChange={handleImageUpload} />
      <button onClick={editingBook ? handleEditBook : handleAddBook}>
        {editingBook ? "Update Book" : "Add Book"}
      </button>

      <h3>Book List</h3>
      {books.map((book) => (
        <div key={book._id}>
          <h4>{book.title}</h4>
          <p>Author: {book.author}</p>
          <p>ISBN: {book.isbn}</p>
          <p>Genre: {book.genre}</p>
          <img src={book.coverImage} alt={book.title} width="100" />
          <button onClick={() => setEditingBook(book)}>Edit</button>
          <button onClick={() => handleDeleteBook(book._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default BookCRUD;
