import React, { useEffect, useState } from "react";
import { fetchBooks, addBook, deleteBook } from "../api";
import EditBookForm from "./EditBookForm";
import { useNavigate } from "react-router";
import axios from "axios";

function BookCRUD() {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [bookDetails, setBookDetails] = useState({ title: "", author: "", image: "" });
  const [editingBook, setEditingBook] = useState(null); // Book to edit
  const token = localStorage.getItem("token");

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleAddBook = async () => {
    try {
      const formData = new FormData();
      formData.append("image", bookDetails.image); // Add image to FormData
      formData.append("title", bookDetails.title);
      formData.append("author", bookDetails.author);

      // Make a POST request to send the book data with the image to the server
      await axios.post("http://localhost:5000/books", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
      });
      alert("Book added successfully!");
      window.location.reload();
    } catch (err) {
      alert("Error adding book");
    }
  };

  const handleImageChange = (e) => {
    console.
    setBookDetails({ ...bookDetails, image: e.target.files[0] }); // Save the image file
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  const handleEditClick = (book) => {
    setEditingBook(book);
  };

  const handleSave = () => {
    setEditingBook(null);
    window.location.reload(); // Refresh book list
  };

  const handleCancel = () => {
    setEditingBook(null);
  };

  const handleDeleteBook = async (id) => {
    try {
      await deleteBook(id, token);
      alert("Book deleted successfully!");
      window.location.reload();
    } catch (err) {
      alert("Error deleting book");
    }
  };

  return (
    <div>
      <button onClick={handleLogout}>Logout</button>
      <h2>Admin: Book CRUD</h2>
      {editingBook ? (
        <EditBookForm
          book={editingBook}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <div>
          <input
            type="text"
            placeholder="Title"
            onChange={(e) => setBookDetails({ ...bookDetails, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Author"
            onChange={(e) => setBookDetails({ ...bookDetails, author: e.target.value })}
          />
          <input
            type="file"
            onChange={handleImageChange}
          />
          <button onClick={handleAddBook}>Add Book</button>
          <h3>Book List:</h3>
          {books.map((book) => (
            <div key={book._id}>
              <h4>{book.title}</h4>
              <p>Author: {book.author}</p>
              {book.image && <img src={`http://localhost:5000/images/${book.image}`} alt="Book" width="100" />}
              <button onClick={() => handleEditClick(book)}>Edit</button>
              <button onClick={() => handleDeleteBook(book._id)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default BookCRUD;
