import React, { useEffect, useState } from "react";
import { fetchBooks, addReview, deleteReview, editReview } from "../api";
import { useNavigate } from "react-router";

function Main() {
  const [books, setBooks] = useState([]);
  const [review, setReview] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  const handleAddReview = async (bookId) => {
    await addReview(bookId, {
      reviewer: localStorage.getItem("username"),
      comment: review,
    });
    alert("Review added!");
    setReview("");
    window.location.reload();
  };

  const handleEditReview = async (bookId, reviewId) => {
    console.log("Editing review with bookId:", bookId, "and reviewId:", reviewId);  // Add this log
    const updatedComment = prompt("Enter your updated review:");
    if (!updatedComment) return;
  
    await editReview(bookId, reviewId, updatedComment);
    alert("Review updated!");
    window.location.reload();
  };
  

  const handleDeleteReview = async (bookId, reviewId) => {
    if (window.confirm("Are you sure you want to delete this review?")) {
      await deleteReview(bookId, reviewId);
      alert("Review deleted!");
      window.location.reload();
    }
  };

  return (
    <div>
      <button onClick={handleLogout}>Logout</button>
      <h2>Book List</h2>
      <input
        type="text"
        placeholder="Search books"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      {books
        .filter((book) =>
          book.title.toLowerCase().includes(searchTerm.toLowerCase())
        )
        .map((book) => (
          <div key={book._id}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <h4>Reviews:</h4>
            {book.reviews.map((review) => (
              <div key={review.id}>
                <p>
                  {review.reviewer}: {review.comment}
                </p>
                <button onClick={() => handleEditReview(book._id, review.id)}>
                  Edit
                </button>
                <button onClick={() => handleDeleteReview(book._id, review.id)}>
                  Delete
                </button>
              </div>
            ))}
            <input
              type="text"
              value={review}
              onChange={(e) => setReview(e.target.value)}
              placeholder="Add a review"
            />
            <button onClick={() => handleAddReview(book._id)}>Add Review</button>
          </div>
        ))}
    </div>
  );
}

export default Main;
