import React, { useEffect, useState } from "react";
import { fetchBooks, addReview, editReview, deleteReview } from "../api";
import { useNavigate } from "react-router";

function Main() {
  const [books, setBooks] = useState([]);
  const [review, setReview] = useState("");
  const [editingReview, setEditingReview] = useState(null); // Track the review being edited
  const navigate = useNavigate();

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  const handleAddReview = async (bookId) => {
    await addReview(bookId, {
      reviewer: localStorage.getItem("username"),
      comment: review,
    });
    alert("Review added!");
    setReview("");
    window.location.reload();
  };

  const handleEditReview = (bookId, existingReview) => {
    setReview(existingReview.comment);
    setEditingReview({ bookId, reviewId: existingReview._id });
  };

  const handleUpdateReview = async () => {
    if (editingReview) {
      await editReview(editingReview.bookId, editingReview.reviewId, {
        comment: review,
      });
      alert("Review updated!");
      setReview("");
      setEditingReview(null); // Reset editing state
      window.location.reload();
    }
  };

  const handleDeleteReview = async (bookId, reviewId) => {
    try {
      await deleteReview(bookId, reviewId);
      alert("Review deleted!");
      window.location.reload();
    } catch (err) {
      alert("Error deleting review");
    }
  };

  return (
    <div>
      <button onClick={handleLogout}>Logout</button>
      <h2>Book List</h2>
      {books.map((book) => (
        <div key={book._id}>
          <h3>{book.title}</h3>
          <p>Author: {book.author}</p>
          <p>ISBN: {book.ISBN}</p>
          <p>Genre: {book.genre}</p>
          <h4>Reviews:</h4>
          {book.reviews.map((rev, index) => (
            <div key={index}>
              <p>
                {rev.reviewer}: {rev.comment}
              </p>
              {rev.reviewer === localStorage.getItem("username") && (
                <>
                  <button onClick={() => handleEditReview(book._id, rev)}>
                    Edit Review
                  </button>
                  <button
                    onClick={() => handleDeleteReview(book._id, rev._id)}
                  >
                    Delete Review
                  </button>
                </>
              )}
            </div>
          ))}
          <input
            type="text"
            value={review}
            placeholder="Add or edit your review"
            onChange={(e) => setReview(e.target.value)}
          />
          {editingReview && editingReview.bookId === book._id ? (
            <button onClick={handleUpdateReview}>Update Review</button>
          ) : (
            <button onClick={() => handleAddReview(book._id)}>Add Review</button>
          )}
        </div>
      ))}
    </div>
  );
}

export default Main;
