import React, { useEffect, useState } from "react";
import { fetchBooks, addBook, deleteBook } from "../api";
import EditBookForm from "./EditBookForm";
import { useNavigate } from "react-router-dom"; // Use to redirect after logout

function BookCRUD() {
  const [books, setBooks] = useState([]);
  const [bookDetails, setBookDetails] = useState({
    title: "",
    author: "",
    ISBN: "",
    genre: ""
  });
  const [editingBook, setEditingBook] = useState(null); // Book to edit
  const token = localStorage.getItem("token");
  const navigate = useNavigate(); // Hook to navigate to other pages

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleAddBook = async () => {
    try {
      await addBook(bookDetails, token);
      alert("Book added successfully!");
      window.location.reload();
    } catch (err) {
      alert("Error adding book");
    }
  };

  const handleEditClick = (book) => {
    setEditingBook(book);
  };

  const handleSave = () => {
    setEditingBook(null);
    window.location.reload(); // Refresh book list
  };

  const handleCancel = () => {
    setEditingBook(null);
  };

  const handleDeleteBook = async (id) => {
    try {
      await deleteBook(id, token);
      alert("Book deleted successfully!");
      window.location.reload();
    } catch (err) {
      alert("Error deleting book");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove the token from localStorage
    alert("You have been logged out.");
    navigate("/login"); // Redirect to login page (change the path as needed)
  };

  return (
    <div>
      <h2>Admin: Book CRUD</h2>
      <button onClick={handleLogout}>Logout</button> {/* Logout button */}
      {editingBook ? (
        <EditBookForm
          book={editingBook}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <div>
          <input
            type="text"
            placeholder="Title"
            onChange={(e) =>
              setBookDetails({ ...bookDetails, title: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Author"
            onChange={(e) =>
              setBookDetails({ ...bookDetails, author: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="ISBN"
            onChange={(e) =>
              setBookDetails({ ...bookDetails, ISBN: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Genre"
            onChange={(e) =>
              setBookDetails({ ...bookDetails, genre: e.target.value })
            }
          />
          <button onClick={handleAddBook}>Add Book</button>
          <h3>Book List:</h3>
          {books.map((book) => (
            <div key={book._id}>
              <h4>{book.title}</h4>
              <p>Author: {book.author}</p>
              <p>ISBN: {book.ISBN}</p>
              <p>Genre: {book.genre}</p>
              <button onClick={() => handleEditClick(book)}>Edit</button>
              <button onClick={() => handleDeleteBook(book._id)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default BookCRUD;
