import React, { useEffect, useState } from "react";
import { fetchBooks, addReview, deleteReview } from "../api";
import { useNavigate } from "react-router";

function Main() {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]); // For filtered results
  const [review, setReview] = useState("");
  const [searchTerm, setSearchTerm] = useState(""); // Search term state
  const navigate = useNavigate();

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
      setFilteredBooks(data); // Initialize filteredBooks with all books
    };
    getBooks();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  const handleAddReview = async (bookId) => {
    await addReview(bookId, { reviewer: localStorage.getItem("username"), comment: review });
    alert("Review added!");
    setReview("");
    window.location.reload();
  };
  const handleEditReview = async (bookId, reviewId) => {
    const updatedComment = prompt("Enter your updated review:");
    if (!updatedComment) return;
  
    await editReview(bookId, reviewId, updatedComment);
    alert("Review updated!");
    window.location.reload();
  };
  
  const handleDeleteReview = async (bookId, reviewId) => {
    if (window.confirm("Are you sure you want to delete this review?")) {
      await deleteReview(bookId, reviewId);
      alert("Review deleted!");
      window.location.reload();
    }
  };
  

  const handleSearch = (term) => {
    setSearchTerm(term);
    const lowerCaseTerm = term.toLowerCase();
    const results = books.filter((book) =>
      book.title.toLowerCase().includes(lowerCaseTerm) ||
      book.author.toLowerCase().includes(lowerCaseTerm) ||
      book.genre.toLowerCase().includes(lowerCaseTerm) ||
      book.ISBN.toLowerCase().includes(lowerCaseTerm)
    );
    setFilteredBooks(results);
  };

  return (
    <div>
      <button onClick={handleLogout}>Logout</button>
      <h2>Book List</h2>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search by title, author, genre, or ISBN"
        value={searchTerm}
        onChange={(e) => handleSearch(e.target.value)}
      />

      {filteredBooks.map((book) => (
        <div key={book._id}>
          <h3>{book.title}</h3>
          <p>Author: {book.author}</p>
          <p>ISBN: {book.ISBN}</p>
          <p>Genre: {book.genre}</p>
          <h4>Reviews:</h4>
          {book.reviews.map((rev) => (
            <div key={rev.id}>
              <p>
                {rev.reviewer}: {rev.comment}
              </p>
              <button onClick={() => handleEditReview(book._id, rev.id)}>Edit</button>
              <button onClick={() => handleDeleteReview(book._id, rev.id)}>Delete</button>
            </div>
          ))}

          <input
            type="text"
            placeholder="Add a review"
            value={review}
            onChange={(e) => setReview(e.target.value)}
          />
          <button onClick={() => handleAddReview(book._id)}>Add Review</button>
        </div>
      ))}
    </div>
  );
}

export default Main;
