import React, { useEffect, useState } from "react";
import { fetchBooks, addBook, deleteBook } from "../api";
import EditBookForm from "./EditBookForm";

function BookCRUD() {
  const [books, setBooks] = useState([]);
  const [bookDetails, setBookDetails] = useState({ title: "", author: "", image: "" });
  const [editingBook, setEditingBook] = useState(null); // Book to edit
  const token = localStorage.getItem("token");

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  // Handle image upload
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file); // Create URL for local image
      setBookDetails({ ...bookDetails, image: imageUrl }); // Save the image URL to state
    }
  };

  // Handle adding a book
  const handleAddBook = async () => {
    try {
      await addBook(bookDetails, token); // Send the book details to the server (image path will not be sent)
      alert("Book added successfully!");
      window.location.reload();
    } catch (err) {
      alert("Error adding book");
    }
  };

  const handleEditClick = (book) => {
    setEditingBook(book);
  };

  const handleSave = () => {
    setEditingBook(null);
    window.location.reload(); // Refresh book list
  };

  const handleCancel = () => {
    setEditingBook(null);
  };

  const handleDeleteBook = async (id) => {
    try {
      await deleteBook(id, token);
      alert("Book deleted successfully!");
      window.location.reload();
    } catch (err) {
      alert("Error deleting book");
    }
  };

  return (
    <div>
      <h2>Admin: Book CRUD</h2>
      {editingBook ? (
        <EditBookForm
          book={editingBook}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <div>
          <input
            type="text"
            placeholder="Title"
            onChange={(e) => setBookDetails({ ...bookDetails, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Author"
            onChange={(e) => setBookDetails({ ...bookDetails, author: e.target.value })}
          />
          <input
            type="file"
            onChange={handleImageChange}
          />
          <div>
            {bookDetails.image && <img src={bookDetails.image} alt="Book Preview" width="100" />}
          </div>
          <button onClick={handleAddBook}>Add Book</button>
          <h3>Book List:</h3>
          {books.map((book) => (
            <div key={book._id}>
              <h4>{book.title}</h4>
              <p>Author: {book.author}</p>
              {book.image && <img src={book.image} alt="Book" width="100" />}
              <button onClick={() => handleEditClick(book)}>Edit</button>
              <button onClick={() => handleDeleteBook(book._id)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default BookCRUD;
