import React, { useEffect, useState } from "react";
import { fetchBooks, addReview, updateReview, deleteReview } from "../api";
import { useNavigate } from "react-router";

function Main() {
  const [books, setBooks] = useState([]);
  const [review, setReview] = useState("");
  const [editReview, setEditReview] = useState("");
  const [editReviewId, setEditReviewId] = useState(null); // Track which review is being edited
  const navigate = useNavigate();

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  const handleAddReview = async (bookId) => {
    if (!review) {
      alert("Review cannot be empty!");
      return;
    }
    await addReview(bookId, { reviewer: localStorage.getItem("username"), comment: review });
    alert("Review added!");
    setReview(""); // Clear review input
    window.location.reload();
  };

  const handleEditReview = (bookId, reviewId, currentComment) => {
    setEditReview(currentComment);
    setEditReviewId(reviewId); // Set review ID to edit
  };

  const handleSaveReview = async (bookId) => {
    if (!editReview) {
      alert("Review cannot be empty!");
      return;
    }
    await updateReview(bookId, editReviewId, { comment: editReview });
    alert("Review updated!");
    setEditReview(""); // Clear edit input
    setEditReviewId(null); // Reset edit review ID
    window.location.reload();
  };

  const handleDeleteReview = async (bookId, reviewId) => {
    await deleteReview(bookId, reviewId);
    alert("Review deleted!");
    window.location.reload();
  };

  return (
    <div>
      <button onClick={handleLogout}>Logout</button>
      <h2>Book List</h2>
      {books.map((book) => (
        <div key={book._id}>
          <h3>{book.title}</h3>
          <p>Author: {book.author}</p>
          <h4>Reviews:</h4>
          {book.reviews.map((rev, index) => (
            <div key={index}>
              <p>
                {rev.reviewer}: {rev.comment}
                {rev.reviewer === localStorage.getItem("username") && (
                  <>
                    <button onClick={() => handleEditReview(book._id, rev._id, rev.comment)}>
                      Edit
                    </button>
                    <button onClick={() => handleDeleteReview(book._id, rev._id)}>Delete</button>
                  </>
                )}
              </p>
            </div>
          ))}

          {/* Display Edit Review Input */}
          {editReviewId && editReviewId !== book._id ? (
            ""
          ) : (
            <div>
              <input
                type="text"
                placeholder="Add a review"
                value={review}
                onChange={(e) => setReview(e.target.value)}
              />
              <button onClick={() => handleAddReview(book._id)}>Add Review</button>
            </div>
          )}
          {/* Edit Review Input */}
          {editReviewId && (
            <div>
              <input
                type="text"
                value={editReview}
                onChange={(e) => setEditReview(e.target.value)}
              />
              <button onClick={() => handleSaveReview(book._id)}>Save Review</button>
              <button onClick={() => setEditReviewId(null)}>Cancel</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default Main;
