import axios from "axios";

export const fetchBookDetails = (bookId) =>
  axios.get(`/api/books/${bookId}`);

export const addReview = (bookId, review) =>
  axios.post(`/api/books/${bookId}/reviews`, review);

export const editReview = (bookId, reviewId, review) =>
  axios.put(`/api/books/${bookId}/reviews/${reviewId}`, review);

export const deleteReview = (bookId, reviewId) =>
  axios.delete(`/api/books/${bookId}/reviews/${reviewId}`);
