import React, { useEffect, useState } from "react";
import { fetchBooks, addBook, editBook, deleteBook } from "../api";

function BookCRUD() {
  const [books, setBooks] = useState([]);
  const [bookDetails, setBookDetails] = useState({ title: "", author: "" });
  const token = localStorage.getItem("token");

  useEffect(() => {
    const getBooks = async () => {
      const { data } = await fetchBooks();
      setBooks(data);
    };
    getBooks();
  }, []);

  const handleAddBook = async () => {
    await addBook(bookDetails, token);
    alert("Book added!");
    window.location.reload();
  };

  const handleEditBook = async (id) => {
    await editBook(id, bookDetails, token);
    alert("Book updated!");
    window.location.reload();
  };

  const handleDeleteBook = async (id) => {
    await deleteBook(id, token);
    alert("Book deleted!");
    window.location.reload();
  };

  return (
    <div>
      <h2>Admin: Book CRUD</h2>
      <input
        type="text"
        placeholder="Title"
        onChange={(e) => setBookDetails({ ...bookDetails, title: e.target.value })}
      />
      <input
        type="text"
        placeholder="Author"
        onChange={(e) => setBookDetails({ ...bookDetails, author: e.target.value })}
      />
      <button onClick={handleAddBook}>Add Book</button>
      {books.map((book) => (
        <div key={book._id}>
          <h3>{book.title}</h3>
          <p>Author: {book.author}</p>
          <button onClick={() => handleEditBook(book._id)}>Edit</button>
          <button onClick={() => handleDeleteBook(book._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default BookCRUD;
